		//æŽ’åºåˆ—ç´¢å¼•,ç›®æ ‡åˆ—çš„element
		_multiSort: function(idxcol, selTh) {
			var ts = this;
			var splas, sort = "",
				cm = ts.p.colModel,
				fs = false,
				ls, so = "";
			$("span.ui-grid-ico-sort", selTh).addClass('ui-state-disabled');
			$(selTh).attr("aria-selected", "false");

			if (cm[idxcol].lso) {
				if (cm[idxcol].lso === "asc") {
					cm[idxcol].lso += "-desc";
					so = "desc";
				} else if (cm[idxcol].lso === "desc") {
					cm[idxcol].lso += "-asc";
					so = "asc";
				} else if (cm[idxcol].lso === "asc-desc" || cm[idxcol].lso === "desc-asc") {
					cm[idxcol].lso = "";
				}
			} else {
				cm[idxcol].lso = so = 'asc';
			}
			if (so) {
				$("span.s-ico", selTh).show();
				$("span.ui-icon-" + so, selTh).removeClass('ui-state-disabled');
				$(selTh).attr("aria-selected", "true");
			} else {
				$("span.s-ico", selTh).hide();
			}
			ts.p.sortorder = "";
			$.each(cm, function(i) {
				if (this.lso) {
					if (i > 0 && fs) {
						sort += ", ";
					}
					splas = this.lso.split("-");
					sort += cm[i].name;
					sort += " " + splas[splas.length - 1];
					fs = true;
					ts.p.sortorder = splas[splas.length - 1];
				}
			});
			ls = sort.lastIndexOf(ts.p.sortorder);
			sort = sort.substring(0, ls);
			ts.p.sortname = sort;
		},

		//idxcol æŽ’åºåˆ—çš„ç´¢å¼•;targetid ç‚¹å‡»åˆ—çš„å¯¹è±¡çš„id,å¯ä»¥æ˜¯å†»ç»“åˆ—,åˆ—idçš„ç»„æˆè§„åˆ™æ˜¯jqgh_gridid_colname;sortorder æŒ‡å®šçš„æŽ’åºæ–¹å¼
		sortData: function(targetid, idxcol, sortorder) {
			var ts = this,
				selectRowIds = [];
			if (!ts.p.colModel[idxcol].sortable) {
				return;
			}
			if (ts.p.savedRow.length > 0) {
				return;
			}

			//èŽ·å–å¤šé€‰è®°å½•
			selectRowIds = ts.p.selarrrow;

			if (sortorder) { //å¦‚æžœä¼ å…¥äº†æŽ’åºæ–¹å¼,å°±ä½¿ç”¨è¿™ç§æ–¹å¼è¿›è¡ŒæŽ’åº;å¦‚æžœæŽ’åºåˆ—åŠæŽ’åºæ–¹å¼ä¸Žä¸Šä¸€æ¬¡ç›¸åŒ,ç›´æŽ¥è¿”å›ž
				if (ts.p.lastsort === idxcol && ts.p.sortorder === sortorder) {
					return;
				}
				ts.p.sortorder = sortorder;
			} else if (ts.p.lastsort === idxcol) {
				if (ts.p.sortorder === 'asc') {
					ts.p.sortorder = 'desc';
				} else if (ts.p.sortorder === 'desc') {
					ts.p.sortorder = 'asc';
				}
			} else {
				ts.p.sortorder = 'asc';
			}

			var previousSelectedTh = ts.grid.headers[ts.p.lastsort].el;
			var newSelectedTh = ts.grid.headers[idxcol].el;

			if (ts.p.multiSort) {
				ts._multiSort(idxcol, newSelectedTh);
			} else {
				$("span.ui-grid-ico-sort", previousSelectedTh).addClass('ui-state-disabled');
				$(previousSelectedTh).attr("aria-selected", "false");
				if (ts.p.frozenColumns) {
					ts.grid.fhDiv.find("span.ui-grid-ico-sort").addClass('ui-state-disabled');
					ts.grid.fhDiv.find("th").attr("aria-selected", "false");
				}
				$("span.ui-icon-" + ts.p.sortorder, newSelectedTh).removeClass('ui-state-disabled');
				$(newSelectedTh).attr("aria-selected", "true");
				if (ts.p.lastsort !== idxcol) {
					if (ts.p.frozenColumns) {
						ts.grid.fhDiv.find("span.s-ico").hide();
					}
					$("span.s-ico", previousSelectedTh).hide();
					$("span.s-ico", newSelectedTh).show();
				}
				ts.p.sortname = targetid.substring(5 + ts.p.id.length + 1); //idçš„ç»„æˆä¸º"jqgh_"+gridId+colName
			}
			if (ts._trigger("onSortCol", null, [ts.p.sortname, idxcol, ts.p.sortorder]) === false) {
				//è¿”å›žtrueä¹‹åŽ,æ˜¯å¦è¦è¿˜åŽŸå˜é‡ts.p.sortorderã€ts.p.sortnameã€ts.p.lastsortç­‰
				ts.p.lastsort = idxcol;
				return;
			}

			if (ts.p.pager && ts.p.curPageSort) {
				ts._curPageSortData(ts.p.sortname, ts.p.sortorder, idxcol);
			} else {
				ts.p._sort = true;
				ts.p.page = 1;
				ts.populatePage();
			}
			ts.p.lastsort = idxcol;
			//æœ¬åœ°åˆ†é¡µè¡¨æ ¼æƒ…å†µä¸‹æ‰æ¢å¤è¡Œå‹¾é€‰
			if (ts.p.datatype === "local" && selectRowIds.length > 0) {
				ts.setCheckRows(selectRowIds, true);
			}
		},

		//å¯¹å½“å‰é¡µæŽ’åºçš„å¤„ç†é€»è¾‘
		_curPageSortData: function(sortName, sortOrder, idxcol) {
			var ts = this,
				i, j, newData = [],
				srcformat = '',
				queryResults,
				curPageData = this.getRowData(), //èŽ·å–å½“å‰é¡µæ•°æ®
				query = $.jgrid.from(curPageData),
				sorttype = ts.p.colModel[idxcol].sorttype || "text";

			if (sorttype === "date" || sorttype === "datetime") {
				srcformat = fish.config.get('dateParseFormat.' + sorttype);
			}
			//èŽ·å–æŽ’åºè¿‡çš„æ•°æ®
			query.orderBy(sortName, sortOrder, sorttype, srcformat);
			queryResults = query.select();

			for (i = 0; i < queryResults.length; i++) {
				var rowId = queryResults[i][ts.p.keyName];
				for (j = 0; j < curPageData.length; j++) {
					if (rowId === curPageData[j][ts.p.keyName]) {
						break;
					}
				}
				var newRow = $(".ui-jqgrid-bdiv", ts.element).find("tr").eq(j + 1);
				newData.push(newRow.clone()[0]);
			}

			for (i = 0; i < newData.length; i++) {
				var row = $(".ui-jqgrid-bdiv", ts.element).find("tr").eq(i + 1);
				row.after(newData[i]).remove();
			}
		},

