		_writeCacheColumnsFeature: function _writeCacheColumnsFeature() {
			var columns = [];
			fish.each(this.options.colModel, function(data) {
				columns.push({
					name: data.name,
					width: data.width,
					hidden: data.hidden
				});
			});
			fish.store.set(this.options.id, JSON.stringify(columns));
		},

